NODES = [4, 8, 16]
RANGE = 3

def plot_multicast_comparison(original, multicast, lat):
    title = "HotStuff - IP Multicast"
    plt.title(title)
    plt.xlabel(r"Nodes")
    plt.ylabel(r"Throughput / second")

    plt.plot(NODES, original, label = "Hotstuff")
    plt.plot(NODES, multicast, label = "Hotstuff - Multicast")
    plt.legend(fontsize = 8)
    plt.savefig("hotstuff_multi_throughput")
    plt.clf()

    plt.title(title)
    plt.xlabel(r"Nodes")
    plt.ylabel(r"Latency ms")

    original_lat = [lat["n4_pay128_b400"][0], lat["n8_pay128_b400"][0], lat["n16_pay128_b400"][0]]
    multicast_lat = [lat["n4_pay128_b400"][1], lat["n8_pay128_b400"][1], lat["n16_pay128_b400"][1]]

    plt.plot(NODES, original_lat, label = "Hotstuff")
    plt.plot(NODES, multicast_lat, label = "Hotstuff - Multicast")
    plt.legend(fontsize = 8)
    plt.savefig("hotstuff_multi_latency")
    plt.clf()

def polt_coh_comparison(original, coh, lat):
    title = "HotStuff - Consensus on Hash"
    plt.title(title)
    plt.xlabel(r"Nodes")
    plt.ylabel(r"Throughput / second")

    plt.plot(NODES, original, label = "Hotstuff")
    plt.plot(NODES, coh, label = "Hotstuff - Consensus on Hash")
    plt.legend(fontsize = 8)
    plt.savefig("hotstuff_coh_throughput")
    plt.clf()

    plt.title(title)
    plt.xlabel(r"Nodes")
    plt.ylabel(r"Latency ms")

    original_lat = [lat["n4_pay128_b400"][0], lat["n8_pay128_b400"][0], lat["n16_pay128_b400"][0]]
    coh_lat = [lat["n4_pay0_b400"][0], lat["n8_pay0_b400"][0], lat["n16_pay0_b400"][0]]

    plt.plot(NODES, original_lat, label = "Hotstuff")
    plt.plot(NODES, coh_lat, label = "Hotstuff - Consensus on Hash")
    plt.legend(fontsize = 8)
    plt.savefig("hotstuff_coh_latency")
    plt.clf()

def plot_coh_multicast_comparison(original, complete, lat):
    title = "HotStuff - Multicast & Consensus on Hash"
    plt.title(title)
    plt.xlabel(r"Nodes")
    plt.ylabel(r"Throughput / second")

    plt.plot(NODES, original, label = "Hotstuff")
    plt.plot(NODES, complete, label = "Hotstuff - Multicast & Consensus on Hash")
    plt.legend(fontsize = 8)
    plt.savefig("hotstuff_multi_coh_throughput")
    plt.clf()

    plt.title(title)
    plt.xlabel(r"Nodes")
    plt.ylabel(r"Latency ms")

    original_lat = [lat["n4_pay128_b400"][0], lat["n8_pay128_b400"][0], lat["n16_pay128_b400"][0]]
    complete_lat = [lat["n4_pay0_b400"][1], lat["n8_pay0_b400"][1], lat["n16_pay0_b400"][1]]

    plt.plot(NODES, original_lat, label = "Hotstuff")
    plt.plot(NODES, complete_lat, label = "Hotstuff - Multicast & Consensus on Hash")
    plt.legend(fontsize = 8)
    plt.savefig("hotstuff_multi_coh_latency")
    plt.clf()

def plot_all(data, lat):
    plot_multicast_comparison(data[1], data[2], lat)
    polt_coh_comparison(data[1], data[3], lat)
    plot_coh_multicast_comparison(data[1], data[4], lat)


def plot_raw(fname, arrays, latency):
    n = fname[1:fname.index("_")]
    payload = "0" if fname.find("pay0") != -1 else "128"
    
    tcp_latency = str(latency[0])
    udp_latency = str(latency[1])

    max_x = 420
    if (n == "4"): max_x = 240

    tcp_data = arrays[0][0:max_x]
    udp_data = arrays[1][0:max_x]

    plt.title(n + " Nodes, Payload " + payload + " B, Block Size 400")
    plt.xlabel(r"time in sec")
    plt.ylabel(r"txn/sec")

    plt.plot(range(len(tcp_data)), tcp_data, label = "TCP,latency=" + tcp_latency + "ms")
    plt.plot(range(len(udp_data)), udp_data, label = "UDP,latency=" + udp_latency + "ms")
    plt.legend(fontsize = 8) # using a size in points

    plt.savefig(fname)

    plt.clf()

